function get(e){return document.querySelector(e)}function showError(e){get("#error").style.display="block",get("#error").innerText=e,setTimeout(()=>error.style.display="none",4e3)}function getNumbersOnlyIncludeBrDecimalFormat(e){if(0==e.length)return"";var t=e.split(",");return 1==t.length?brNumberFormat(t[0]):brNumberFormat(t[0])+","+getNumbersOnly(t[1]).substring(0,2)}function getFromBrDecimalToNumber(e){if(0==e.length)return 0;var t=e.split(",");return 1==t.length?Number(getNumbersOnly(e)):Number(getNumbersOnly(t[0])+"."+getNumbersOnly(t[1]))}function addValue(e,t){if(0==e.length)return t>0?t+"":"";var r=e.split(","),n=Number(getNumbersOnly(r[0])),o=n+t,u=o<0?"0":o+"";return 1==r.length?brNumberFormat(u):brNumberFormat(u)+","+r[1]}function getCurrency(e){return new Intl.NumberFormat("pt-BR",{style:"currency",currency:"BRL"}).format(e)}function getPtcFormat(e){return new Intl.NumberFormat("pt-BR",{style:"percent",minimumFractionDigits:2,maximumFractionDigits:2}).format(e)}function getNumbersOnly(e){let t=e.length;return 0==t?"":e.replace(/[^0-9]/g,"")}function brNumberFormat(e){let t=getNumbersOnly(e);return t.length>0?new Intl.NumberFormat("pt-BR").format(t):""}class HTTP{get(e){return new Promise((t,r)=>{fetch(e).then(e=>e.json()).then(e=>t(e)).catch(e=>r(e))})}post(e,t){return new Promise((r,n)=>{fetch(e,{method:"POST",headers:{"Content-type":"application/json"},body:JSON.stringify(t)}).then(e=>e.json()).then(e=>r(e)).catch(e=>n(e))})}put(e,t){return new Promise((r,n)=>{fetch(e,{method:"PUT",headers:{"Content-type":"application/json"},body:JSON.stringify(t)}).then(e=>e.json()).then(e=>r(e)).catch(e=>n(e))})}delete(e){return new Promise((t,r)=>{fetch(e,{method:"PUT",headers:{"Content-type":"application/json"}}).then(e=>e.json()).then(e=>t(e)).catch(e=>r(e))})}}